import React,{Component} from 'react';

class Lista extends Component{

    constructor(props){

        super(props);
        this.state = {
            id:"",
            entrada:"",
            items:[]
        }


       this.updateState = this.updateState.bind(this);
       this.keyEnter = this.keyEnter.bind(this);



    }

   updateState = e => {                 



            this.setState({entrada: e.target.value});


   }

   keyEnter = e => {

        if(e.keyCode === 13){

            e.preventDefault();            
            this.setState({entrada:"",items:[...this.state.items,this.state.entrada]})

         }


   }




    render(){

     return ( 



            <div>

        <div class="input-group mb-3">
        <div class="input-group-prepend">
          <input class="btn btn-outline-secondary" type="checkbox" />

        </div>


             <input 
                        name="lentrada"
                        type="text"
                        value={this.state.entrada}
                        className="form-control"
                        aria-label="Large"
                        onChange={this.updateState}
                        onKeyDown={this.keyEnter}
                        autofocus="true" 
                    />    



      </div>


                  <div>

                {this.state.items.map((item) => (

                                    {item} 


                ))}     
                   </div>     
                   </div>  
     )
    }
}

export default Lista;
