import React,{Component} from 'react';

class Lista extends Component{

    constructor(props){

        super(props);


        this.state = {
            id:"",
            entrada:"",
            items:[]
        }




        axios.get('localhost:8080/api/todoModels')
        .then(res => {
          const tmodels = res.data;
          this.setState({ tmodels });
        })


       this.updateState = this.updateState.bind(this);
       this.keyEnter = this.keyEnter.bind(this);



    }

   updateState = e => {                 



            this.setState({entrada: e.target.value});


   }

   keyEnter = e => {

        if(e.keyCode === 13){

            e.preventDefault();            
            this.setState({entrada:"",items:[...this.state.items,this.state.entrada]})

         }


   }




    render(){

     return (       


        <div>            

             <input 
                        name="lentrada"
                        type="text"
                        value={this.state.entrada}
                        className="form-control"
                        aria-label="Large"
                        onChange={this.updateState}
                        onKeyDown={this.keyEnter}
                        autofocus="true" 
                    />    



     <table className="table">


            {this.state.items.map((item,s) => (


            <tr> 
              <td><input type="checkbox" /> </td>
              <td>  {item}   </td>
              <td>  <button> Cancelar  </button></td>
            </tr>




                                      ))}     


                                     </table>
      </div> 



     )
    }
}

export default Lista;
